class MarvelService {
    _apiBase = 'https://gateway.marvel.com:443/v1/public/'; // Таке оголошення змінних між програмістами використовується для тих змінних, які ні за яких обставин не можна змінювати
    _apiKey = 'apikey=72f4e1a4e83c1348a14e7362c4bea1c0';

    getResource = async (url) => {
        let res = await fetch(url);

        if(!res.ok) {
            throw new Error(`Could not fetch ${url}, status: ${res.status}`);
        }

        return await res.json();
    }

    getAllCharacters = () => {
        return this.getResource(`https://gateway.marvel.com:443/v1/public/characters?limit=9&offset=210&apikey=72f4e1a4e83c1348a14e7362c4bea1c0`);
    }

    getCharacter = (id) => {
        return this.getResource(`${this._apiBase}${id}?${this._apiKey}`);
    }
}

export default MarvelService;
